import React, {useState,useEffect,useRef} from 'react';
import socketIOClient from 'socket.io-client';
import ToDoList from "./ToDoList"
import './ToDoList.css';

import {Link} from 'react-router-dom'

import OurTimer from './Timer';

const SOCKET_SERVER_URL = 'http://localhost:5000';
const PASSWORD_PLEASE = 'requestPassword';

const Collab = (props) => {
  const {roomId} =  props.match.params;
  const [askForPassword, setAskForPassword] = useState(true);
  const socketRef = useRef();
  const [password, setPassword] = useState('')

  useEffect(()=>{
    socketRef.current = socketIOClient(SOCKET_SERVER_URL, {
      query:{roomId},
    });

    socketRef.current.on(PASSWORD_PLEASE, (data) => {
      console.log('password data',JSON.stringify(data))
    })
  })

  const handleChange = (e) => {
    setPassword(e.target.value);
}

  if (askForPassword) {
    return (
      <div>
        <p>PASSWORD:</p>
        <input
          onchange = {handleChange}
          value = {password}
        ></input>
      </div>
    );
  } else {
    return (
      <div className="App">
          <div className="App-header">
            <header className='collab-header'>
              <Link to ={`/`} className='home-button'> 
                <h1 className='pomodoer'>
                  POMODOER
                </h1> 
              </Link>
            </header>
         </div>
          <div className='timer'>
            <OurTimer room={roomId}/> 
          </div>
          <h2>ROOM: {roomId}</h2>
          <ToDoList room={roomId}/>

      </div>
    );
  }
}

export default Collab;
